from flask import Flask, request

app = Flask(__name__)

# Example middleware (before_request)
@app.before_request
def before_request():
    # Perform actions before each request, e.g., logging
    print(f'Before Request - Path: {request.path}')

# Example middleware (after_request)
@app.after_request
def after_request(response):
    # Perform actions after each request, e.g., modifying the response
    print(f'After Request - Status Code: {response.status_code}')
    return response

