from flask import Flask, Blueprint, render_template

app = Flask(__name__)

# Create a blueprint
sample_blueprint = Blueprint('sample', __name__)

# Define routes within the blueprint
@sample_blueprint.route('/')
def index():
    return 'Sample Blueprint Index'

@sample_blueprint.route('/about')
def about():
    return 'About Us (Sample Blueprint)'

# Register the blueprint with the Flask app
app.register_blueprint(sample_blueprint, url_prefix='/sample')

if __name__ == '__main__':
    app.run(debug=True)

from flask import Flask, request, session

app = Flask(__name__)

# Set a secret key for session security (change this to a secure value)
app.secret_key = 'your_secret_key'

# Route to set a session variable
@app.route('/set_session/<value>')
def set_session(value):
    session['username'] = value
    return f'Session variable username set to {value}'

# Route to retrieve and display the session variable
@app.route('/get_session')
def get_session():
    username = session.get('username')
    if username is None:
        return 'Session variable username is not set.'
    else:
        return f'Session variable username is set to {username}'

# Route to clear the session
@app.route('/clear_session')
def clear_session():
    session.pop('username', None)
    return 'Session cleared'

if __name__ == '__main__':
    app.run(debug=True)

