def my_decorator(func):
    def wrapper():
        print('Something is happening before the function is called.')
        func()
        print('Something is happening after the function is called.')
    return wrapper

@my_decorator
    def say_hello():
        print('Hello!')

say_hello()

def countdown(n):
    while n > 0:
        yield n
        n -= 1

for i in countdown(5):
    print(i)

class MyMeta(type):
    def __new__(cls, name, bases, dct):
        # Modify or validate class attributes before class creation
        return super().__new__(cls, name, bases, dct)

class MyClass(metaclass=MyMeta):
    pass

class DescriptorExample:
    def __get__(self, instance, owner):
        print('Getting the attribute')
        return instance._value

    def __set__(self, instance, value):
        print('Setting the attribute')
        instance._value = value

class MyClass:
    descriptor = DescriptorExample()

obj = MyClass()
obj.descriptor = 42  # Calls the __set__ method
print(obj.descriptor)  # Calls the __get__ method

class MyContextManager:
    def __enter__(self):
        print('Entering the context')

    def __exit__(self, exc_type, exc_value, traceback):
        print('Exiting the context')

with MyContextManager():
    print('Inside the context')

