from flask import Flask, request, session, redirect, url_for
app = Flask(__name__)
app.secret_key = 'your_secret_key_here'
@app.route('/login', methods=['POST'])
def login():
    username = request.form['username']
    password = request.form['password']
    if username == 'example' and password == 'password':
        session['username'] = username
        return redirect(url_for('dashboard'))
    else:
        return 'Invalid credentials'
@app.route('/dashboard')
def dashboard():
    if 'username' in session:
        username = session['username']
        return f'Welcome, {username}! This is your dashboard.'
    else:
        return redirect(url_for('login'))
@app.route('/logout')
def logout():
    session.pop('username', None)
    return redirect(url_for('login'))
if __name__ == '__main__':
    app.run(debug=True)

const express = require('express');
const session = require('express-session');
const app = express();
app.use(session({ secret: 'your_secret_key_here', resave: false, saveUninitialized: true }));
app.post('/login', (req, res) => {
    const { username, password } = req.body;
    if (username === 'example' && password === 'password') {
        req.session.username = username;
        res.redirect('/dashboard');
    } else {
        res.send('Invalid credentials');
    }
});
app.get('/dashboard', (req, res) => {
    if (req.session.username) {
        res.send(`Welcome, ${req.session.username}! This is your dashboard.`);
    } else {
        res.redirect('/login');
    }
});
app.get('/logout', (req, res) => {
    req.session.destroy(() => {
        res.redirect('/login');
    });
});
app.listen(3000, () => {
    console.log('Server is running on port 3000');
});
